import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import CustomButton from "../../components/custom-button/custom-button.component";
import Scan from "../../components/scan/scan.component.jsx";
import { addItem } from "../../redux/cart/cart.actions";
import { selectCartItems } from "../../redux/cart/cart.selectors";
import {
  CollectionItemContainer,
  CollectionFooterContainer,
  AddButton,
  BackgroundImage,
  NameContainer,
  PriceContainer,
} from "../../components/collection-item/collection-item.styles";

class ScannerPage extends Component {
  state = {
    scanning: false,
    results: null,
    item: null,
  };

  _scan = () => {
    this.setState({ scanning: !this.state.scanning });
  };

  onDetected = (result) => {
    const getProduct = async (result) => {
      try {
        const response = await axios.get(
          "http://localhost:5000/scan/" + result
        );
        const items = response;
        return items;
      } catch (error) {
        alert(error); // catches both errors
      }
    };

    this.setState({ results: result.codeResult.code });
    getProduct(result.codeResult.code).then((res) =>
      this.setState({ item: res.data.data })
    );
  };

  renderResult = ({ item, addItem }) => {
    //return <div>{result}</div>;
    if (item) {
      const { name, price, imageUrl } = item;

      return (
        <CollectionItemContainer>
          <BackgroundImage className="image" imageUrl={imageUrl} />
          <CollectionFooterContainer>
            <NameContainer>{name}</NameContainer>
            <PriceContainer>{price}</PriceContainer>
          </CollectionFooterContainer>
          <AddButton onClick={() => addItem(item)} inverted>
            Add to cart
          </AddButton>
        </CollectionItemContainer>
      );
    } else {
      return <div>Loading...</div>;
    }
  };

  render() {
    return (
      <div>
        <CustomButton type="button" onClick={this._scan}>
          {this.state.scanning ? "Stop" : "Start"}
        </CustomButton>
        <div className="results">
          {(addItem) => this.renderResult(this.state.item)}
        </div>
        {this.state.scanning ? <Scan onDetected={this.onDetected} /> : null}
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  cartItems: selectCartItems,
});

//export default ScannerPage;
//export default connect(null, mapDispatchToProps)(ScannerPage);
const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ScannerPage);
