import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import CustomButton from "../../components/custom-button/custom-button.component";
import Scan from "../../components/scan/scan.component.jsx";
import { addItem } from "../../redux/cart/cart.actions";
import { selectCartItems } from "../../redux/cart/cart.selectors";

class ScannerPage extends Component {
  state = {
    scanning: false,
    results: null,
  };

  _scan = () => {
    this.setState({ scanning: !this.state.scanning });
  };

  _onDetected = (result) => {
    this.setState({ results: result.codeResult.code });
    getProduct(result.codeResult.code).then((res) =>
      console.log(res.data.data)
    );
  };

  getProduct = async (result) => {
    try {
      const response = await axios.get("http://localhost:5000/scan/" + result);
      const items = response;
      return items;
    } catch (error) {
      alert(error); // catches both errors
    }
  };

  render() {
    return (
      <div>
        <CustomButton type="button" onClick={this._scan}>
          {this.state.scanning ? "Stop" : "Start"}
        </CustomButton>
        <div className="results">{this.state.results}</div>
        {this.state.scanning ? <Scan onDetected={this._onDetected} /> : null}
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  cartItems: selectCartItems,
});

//export default ScannerPage;
//export default connect(null, mapDispatchToProps)(ScannerPage);
const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ScannerPage);
